import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import dayjs from "dayjs";

const Profile = () => {
  const [items, setItems] = useState([]);
  const [user, setUser] = useState(null);
  const [drivers, setDrivers] = useState([]);
  const [selectedDriver, setSelectedDriver] = useState("");
  const apiUrl = "https://bam-app-489c6c1370a9.herokuapp.com";

  // Получение данных пользователя и созданных им элементов
  useEffect(() => {
    const fetchData = async () => {
      try {
        const username = localStorage.getItem("username");
        setUser(username);

        const response = await axios.get(`${apiUrl}/api/data`);
        const userItems = response.data.filter(
          (item) => item.user === username
        );
        setItems(userItems);

        // Убираем дубликаты из списка водителей
        const driverList = [
          ...new Set(response.data.map((item) => item.driver)),
        ];
        setDrivers(driverList);
      } catch (error) {
        console.error("Ошибка при загрузке данных:", error);
      }
    };

    fetchData();
  }, []);

  if (!user) {
    return <p>Загрузка...</p>;
  }

  const handleDriverChange = (event) => {
    setSelectedDriver(event.target.value);
  };

  return (
    <div>
      <Link to="/">
        <button>Go to Home</button>
      </Link>
      <h1>Профиль пользователя: {user}</h1>
      <p>Количество созданных элементов: {items.length}</p>
      <label htmlFor="driver-select">Выберите водителя:</label>
      <select
        id="driver-select"
        value={selectedDriver}
        onChange={handleDriverChange}
      >
        <option value="">Выберите водителя</option>
        {drivers.map((driver, index) => (
          <option key={index} value={driver}>
            {driver}
          </option>
        ))}
      </select>
      {/* Здесь можно добавить дополнительный контент, связанный с выбранным водителем */}
    </div>
  );
};

export default Profile;
