import React, { useState, useEffect } from "react";
import Confetti from "react-confetti";

const NewYearPopup = () => {
  const [showPopup, setShowPopup] = useState(true); // Стейт для отображения попапа
  const [confetti, setConfetti] = useState(false); // Стейт для включения конфетти

  // Запуск конфетти и его длительность
  useEffect(() => {
    // Делаем конфетти бесконечным при первом рендере
    if (confetti) {
      const confettiTimer = setTimeout(() => {
        setConfetti(false); // Останавливаем конфетти через 3 минуты
      }, 180000); // 180000 миллисекунд = 3 минуты

      return () => clearTimeout(confettiTimer); // Очищаем таймер при размонтировании компонента
    }
  }, [confetti]);

  // Обработчик нажатия на кнопку
  const handleButtonClick = () => {
    setShowPopup(false); // Скрываем попап при клике
    setConfetti(true); // Включаем конфетти
  };

  return (
    <>
      {showPopup && (
        <div className="popup-overlay">
          {confetti && <Confetti />} {/* Показываем конфетти */}
          <div className="popup-content">
            <img
              className="popup-img"
              src="https://your-image-url.com" // Замените на URL вашей картинки с Дедом Морозом
              alt="С Новым годом!"
            />
            <h2>С Новым годом!</h2>
            <p>Пусть этот год принесет счастье и радость!</p>
            <button className="popup-button" onClick={handleButtonClick}>
              Нажми на меня
            </button>
          </div>
        </div>
      )}
    </>
  );
};

export default NewYearPopup;
