import React, { useState, useEffect } from "react";
import axios from "axios";

const Home = () => {
  const [data, setData] = useState([]);
  const [newItem, setNewItem] = useState({ field1: "", field2: "" });
  const [currentUser, setCurrentUser] = useState("user123"); // Пример текущего пользователя

  useEffect(() => {
    // Загрузка данных при монтировании компонента
    axios
      .get("http://localhost:5000/api/data")
      .then((response) => setData(response.data))
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  const handleInputChange = (e, itemId, fieldName) => {
    const { value, type, checked } = e.target;
    const updatedValue = type === "checkbox" ? checked : value;
    const updatedItem = data.find((item) => item._id === itemId);

    setData(
      data.map((item) =>
        item._id === itemId ? { ...item, [fieldName]: updatedValue } : item
      )
    );

    axios
      .put(`http://localhost:5000/api/data/${itemId}`, {
        ...updatedItem,
        [fieldName]: updatedValue,
        changedBy: currentUser, // Добавляем пользователя
      })
      .catch((error) => console.error("Error saving data:", error));
  };

  const handleDateChange = (e, itemId) => {
    const { value } = e.target;
    const updatedItem = data.find((item) => item._id === itemId);

    setData(
      data.map((item) =>
        item._id === itemId ? { ...item, date: value } : item
      )
    );

    axios
      .put(`http://localhost:5000/api/data/${itemId}`, {
        ...updatedItem,
        date: value,
        changedBy: currentUser, // Добавляем пользователя
      })
      .catch((error) => console.error("Error updating date:", error));
  };

  const addNewItem = async () => {
    try {
      const response = await axios.post("http://localhost:5000/api/data", {
        ...newItem,
        changedBy: currentUser, // Добавляем пользователя
      });
      setData([...data, response.data]);
      setNewItem({ field1: "", field2: "" }); // Очистка формы
    } catch (error) {
      console.error("Error adding data:", error);
    }
  };

  return (
    <div>
      <h1>Data Management</h1>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          addNewItem();
        }}
      >
        <input
          type="text"
          value={newItem.field1}
          onChange={(e) => setNewItem({ ...newItem, field1: e.target.value })}
          placeholder="Field 1"
        />
        <input
          type="text"
          value={newItem.field2}
          onChange={(e) => setNewItem({ ...newItem, field2: e.target.value })}
          placeholder="Field 2"
        />
        <button type="submit">Add New Item</button>
      </form>
      <ul>
        {data.map((item) => (
          <li key={item._id}>
            <input
              type="text"
              value={item.field1}
              onChange={(e) => handleInputChange(e, item._id, "field1")}
            />
            <input
              type="text"
              value={item.field2}
              onChange={(e) => handleInputChange(e, item._id, "field2")}
            />
            <input
              type="date"
              value={item.date}
              onChange={(e) => handleDateChange(e, item._id)}
            />
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Home;
