import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
  Link,
} from "react-router-dom";
import Login from "./components/Login";
import Register from "./components/Register";
import Home from "./home";
import Profile from "./components/Profile";

const App = () => {
  const [auth, setAuth] = useState({
    isAuthenticated: false,
    user: null,
  });

  useEffect(() => {
    const checkAuth = async () => {
      const token = localStorage.getItem("token");
      const userName = localStorage.getItem("username");
      if (token) {
        try {
          setAuth({
            isAuthenticated: true,
            user: userName,
          });
        } catch (error) {
          console.error("Error fetching user data:", error);
          localStorage.removeItem("token");
          localStorage.removeItem("username");
          setAuth({
            isAuthenticated: false,
            user: null,
          });
        }
      }
    };

    checkAuth();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("username");
    setAuth({ isAuthenticated: false, user: null });
    <Link to="/"></Link>;
  };

  return (
    <Router>
      <div>
        {auth.isAuthenticated && (
          <nav className="nav-profile">
            <span className="username">{localStorage.getItem("username")}</span>
            <Link to="/profile">
              <button>Go to Profile</button>
            </Link>
            <button onClick={handleLogout}>Logout</button>
            <div className="developer">
              <a href="https://t.me/web_gpy" target="_blanc">
                &copy; Web-GPY Software.
              </a>{" "}
              2024 Все права защищены.
            </div>
          </nav>
        )}
        <Routes>
          <Route
            path="/login"
            element={
              auth.isAuthenticated ? (
                <Navigate to="/" />
              ) : (
                <Login setAuth={setAuth} />
              )
            }
          />
          <Route
            path="/register"
            element={auth.isAuthenticated ? <Navigate to="/" /> : <Register />}
          />
          <Route
            path="/"
            element={auth.isAuthenticated ? <Home /> : <Navigate to="/login" />}
          />
          <Route
            path="/profile"
            element={
              auth.isAuthenticated ? <Profile /> : <Navigate to="/profile" />
            }
          />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
