import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { hardcodedData } from "./hardcodedData";
import "./styles.css";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const [data, setData] = useState([]);
  const [drivers, setDrivers] = useState([]);
  const [newDriver, setNewDriver] = useState("");
  const [newItem, setNewItem] = useState({
    date: new Date().toISOString().split("T")[0],
    brand: "",
    driver: "",
    customer: "",
    routeNumber: "",
    hours: "",
    isTrue: false,
    vehicleNumber: "",
    price: "",
    colorClass: "",
  });
  const [selectedDate, setSelectedDate] = useState(
    new Date().toISOString().split("T")[0]
  );
  const navigate = useNavigate();
  const currentDayRef = useRef(null);
  const [activeInput, setActiveInput] = useState(null);
  const [editInfo, setEditInfo] = useState({}); // Состояние для хранения информации о редактировании

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    const fetchDrivers = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/drivers");
        setDrivers(response.data);
      } catch (error) {
        console.error("Error fetching drivers:", error);
      }
    };

    fetchData();
    fetchDrivers();
  }, []);

  useEffect(() => {
    if (currentDayRef.current) {
      currentDayRef.current.scrollIntoView({ behavior: "smooth", block: "start" });
    }
  }, [data]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  const replaceSoftSign = (str) => {
    return str.replace(/ъ/g, '');
  };

  const sortedData = data
    .sort((a, b) => {
      if (a.colorClass === 'highlight' && b.colorClass !== 'highlight') return 1;
      if (a.colorClass !== 'highlight' && b.colorClass === 'highlight') return -1;

      const driverComparison = a.driver.localeCompare(b.driver);
      if (driverComparison !== 0) return driverComparison;

      return new Date(a.date) - new Date(b.date);
    });

  const addEntriesForSelectedDate = async () => {
    try {
      const newEntries = hardcodedData.map((item) => ({
        ...item,
        date: selectedDate,
      }));

      await Promise.all(
        newEntries.map((entry) =>
          axios.post("http://localhost:5000/api/data", entry)
        )
      );

      const response = await axios.get("http://localhost:5000/api/data");
      setData(response.data);
    } catch (error) {
      console.error("Error adding entries:", error);
    }
  };

  const addNewItem = async () => {
    try {
      const response = await axios.post("http://localhost:5000/api/data", newItem);
      setData([...data, response.data]);
    } catch (error) {
      console.error("Error adding data:", error);
    }
  };

  const handleInputChange = (e, itemId, fieldName) => {
    const { value, type, checked } = e.target;
    const updatedValue = type === "checkbox" ? checked : value;

    // Обновление состояния данных
    const updatedData = data.map((item) =>
      item._id === itemId ? { ...item, [fieldName]: updatedValue } : item
    );
    setData(updatedData);

    // Обновление информации о редактировании
    setEditInfo((prev) => ({
      ...prev,
      [itemId]: {
        [fieldName]: `Last edited by: ${localStorage.getItem("username")} at ${new Date().toLocaleTimeString()}`,
      },
    }));

    // Отправка обновленных данных на сервер
    axios
      .put(`http://localhost:5000/api/data/${itemId}`, {
        ...updatedData.find((item) => item._id === itemId),
        [fieldName]: updatedValue,
      })
      .catch((error) => console.error("Error saving data:", error));
  };

  const addNewItemWithClass = async (className) => {
    try {
      const response = await axios.post("http://localhost:5000/api/data", {
        ...newItem,
        colorClass: className,
      });
      setData([...data, response.data]);
    } catch (error) {
      console.error("Error adding data with class:", error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/data/${id}`);
      setData(data.filter(item => item._id !== id));
    } catch (error) {
      console.error("Error deleting data:", error);
    }
  };

  const handleCheckboxChange = async (e, itemId) => {
    const { checked } = e.target;

    // Показываем предупреждение перед изменением состояния чекбокса
    const isConfirmed = window.confirm("Вы уверены, что хотите изменить состояние этого чекбокса?");
    if (!isConfirmed) {
      return;
    }

    // Обновление состояния данных
    const updatedData = data.map((item) =>
      item._id === itemId ? { ...item, isTrue: checked } : item
    );
    setData(updatedData);

    try {
      // Отправка обновленного значения на сервер
      await axios.put(`http://localhost:5000/api/data/${itemId}`, {
        ...updatedData.find((item) => item._id === itemId),
        isTrue: checked,
      });
    } catch (error) {
      console.error("Error updating data:", error);
    }
  };

  const showWarning = (message) => {
    return window.confirm(message);
  };

  const handleDeleteWithConfirmation = async (id) => {
    const isConfirmed = window.confirm("Are you sure you want to delete this item?");
    if (isConfirmed) {
      try {
        await axios.delete(`http://localhost:5000/api/data/${id}`);
        setData(data.filter(item => item._id !== id));
      } catch (error) {
        console.error("Error deleting data:", error);
      }
    }
  };

  const handleDateChange = (e, itemId) => {
    const { value } = e.target;

    const updatedData = data.map((item) =>
      item._id === itemId ? { ...item, date: value } : item
    );
    setData(updatedData);

    axios
      .put(`http://localhost:5000/api/data/${itemId}`, {
        ...updatedData.find((item) => item._id === itemId),
        date: value,
      })
      .catch((error) => console.error("Error updating date:", error));
  };

  const handleAddNewDriver = async () => {
    if (newDriver.trim() === "") return;

    try {
      const response = await axios.post("http://localhost:5000/api/drivers", {
        name: newDriver,
      });
      setDrivers([...drivers, response.data]);
      setNewDriver("");
    } catch (error) {
      console.error("Error adding driver:", error);
    }
  };

  const groupByMonthAndDay = (data) => {
    return data.reduce((groups, item) => {
      const date = new Date(item.date);
      const monthYear = date.toLocaleString("default", { month: "long" });
      const day = date.getDate();

      if (!groups[monthYear]) groups[monthYear] = {};
      if (!groups[monthYear][day]) groups[monthYear][day] = [];

      groups[monthYear][day].push(item);
      return groups;
    }, {});
  };

  const isToday = (itemDate) => {
    const today = new Date();
    const itemDay = new Date(itemDate);
    return (
      today.getDate() === itemDay.getDate() &&
      today.getMonth() === itemDay.getMonth() &&
      today.getFullYear() === itemDay.getFullYear()
    );
  };

  const getMonthName = (date) => {
    return new Intl.DateTimeFormat('ru-RU', { month: 'long' }).format(date);
  };

  return (
    <div>
      <div className="header">
        <h1>Data Table</h1>
        <button onClick={handleLogout}>Logout</button>
      </div>
      <div>
        <label>
          New Driver:
          <input
            type="text"
            value={newDriver}
            onChange={(e) => setNewDriver(e.target.value)}
          />
        </label>
        <button onClick={handleAddNewDriver}>Add Driver</button>
      </div>
      <div>
        <label>
          Select Date:
          <input
            type="date"
            value={selectedDate}
            onChange={(e) => setSelectedDate(e.target.value)}
          />
        </label>
        <button onClick={addEntriesForSelectedDate}>Add Entries</button>
      </div>
      <div>
        <label>
          Brand:
          <input
            type="text"
            value={newItem.brand}
            onChange={(e) => setNewItem({ ...newItem, brand: e.target.value })}
          />
        </label>
        <label>
          Driver:
          <select
            value={newItem.driver}
            onChange={(e) => setNewItem({ ...newItem, driver: e.target.value })}
          >
            <option value="">Select Driver</option>
            {drivers.map((driver) => (
              <option key={driver._id} value={driver.name}>
                {driver.name}
              </option>
            ))}
          </select>
        </label>
        <label>
          Customer:
          <input
            type="text"
            value={newItem.customer}
            onChange={(e) => setNewItem({ ...newItem, customer: e.target.value })}
          />
        </label>
        <label>
          Route Number:
          <input
            type="text"
            value={newItem.routeNumber}
            onChange={(e) => setNewItem({ ...newItem, routeNumber: e.target.value })}
          />
        </label>
        <label>
          Hours:
          <input
            type="text"
            value={newItem.hours}
            onChange={(e) => setNewItem({ ...newItem, hours: e.target.value })}
          />
        </label>
        <label>
          Vehicle Number:
          <input
            type="text"
            value={newItem.vehicleNumber}
            onChange={(e) => setNewItem({ ...newItem, vehicleNumber: e.target.value })}
          />
        </label>
        <label>
          Price:
          <input
            type="text"
            value={newItem.price}
            onChange={(e) => setNewItem({ ...newItem, price: e.target.value })}
          />
        </label>
        <label>
          Color Class:
          <input
            type="text"
            value={newItem.colorClass}
            onChange={(e) => setNewItem({ ...newItem, colorClass: e.target.value })}
          />
        </label>
        <button onClick={addNewItem}>Add New Item</button>
      </div>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Brand</th>
            <th>Driver</th>
            <th>Customer</th>
            <th>Route Number</th>
            <th>Hours</th>
            <th>Vehicle Number</th>
            <th>Price</th>
            <th>Color Class</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {Object.keys(groupByMonthAndDay(data)).map((month) => (
            <React.Fragment key={month}>
              <tr>
                <td colSpan="10" className="month-header">
                  {getMonthName(new Date(month))}
                </td>
              </tr>
              {Object.keys(groupByMonthAndDay(data)[month]).map((day) => (
                <React.Fragment key={day}>
                  <tr ref={isToday(new Date()) ? currentDayRef : null}>
                    <td colSpan="10" className="day-header">
                      {day}
                    </td>
                  </tr>
                  {groupByMonthAndDay(data)[month][day].map((item) => (
                    <tr key={item._id} className={item.colorClass}>
                      <td>
                        <input
                          type="date"
                          value={item.date}
                          onChange={(e) => handleDateChange(e, item._id)}
                        />
                      </td>
                      <td>
                        <input
                          type="text"
                          value={item.brand}
                          onChange={(e) => handleInputChange(e, item._id, "brand")}
                          onFocus={() => setActiveInput(item._id)}
                        />
                        {activeInput === item._id && editInfo[item._id]?.brand && (
                          <div className="tooltip">
                            {editInfo[item._id].brand}
                          </div>
                        )}
                      </td>
                      <td>
                        <select
                          value={item.driver}
                          onChange={(e) => handleInputChange(e, item._id, "driver")}
                          onFocus={() => setActiveInput(item._id)}
                        >
                          <option value="">Select Driver</option>
                          {drivers.map((driver) => (
                            <option key={driver._id} value={driver.name}>
                              {driver.name}
                            </option>
                          ))}
                        </select>
                        {activeInput === item._id && editInfo[item._id]?.driver && (
                          <div className="tooltip">
                            {editInfo[item._id].driver}
                          </div>
                        )}
                      </td>
                      <td>
                        <input
                          type="text"
                          value={item.customer}
                          onChange={(e) => handleInputChange(e, item._id, "customer")}
                          onFocus={() => setActiveInput(item._id)}
                        />
                        {activeInput === item._id && editInfo[item._id]?.customer && (
                          <div className="tooltip">
                            {editInfo[item._id].customer}
                          </div>
                        )}
                      </td>
                      <td>
                        <input
                          type="text"
                          value={item.routeNumber}
                          onChange={(e) => handleInputChange(e, item._id, "routeNumber")}
                          onFocus={() => setActiveInput(item._id)}
                        />
                        {activeInput === item._id && editInfo[item._id]?.routeNumber && (
                          <div className="tooltip">
                            {editInfo[item._id].routeNumber}
                          </div>
                        )}
                      </td>
                      <td>
                        <input
                          type="text"
                          value={item.hours}
                          onChange={(e) => handleInputChange(e, item._id, "hours")}
                          onFocus={() => setActiveInput(item._id)}
                        />
                        {activeInput === item._id && editInfo[item._id]?.hours && (
                          <div className="tooltip">
                            {editInfo[item._id].hours}
                          </div>
                        )}
                      </td>
                      <td>
                        <input
                          type="text"
                          value={item.vehicleNumber}
                          onChange={(e) => handleInputChange(e, item._id, "vehicleNumber")}
                          onFocus={() => setActiveInput(item._id)}
                        />
                        {activeInput === item._id && editInfo[item._id]?.vehicleNumber && (
                          <div className="tooltip">
                            {editInfo[item._id].vehicleNumber}
                          </div>
                        )}
                      </td>
                      <td>
                        <input
                          type="text"
                          value={item.price}
                          onChange={(e) => handleInputChange(e, item._id, "price")}
                          onFocus={() => setActiveInput(item._id)}
                        />
                        {activeInput === item._id && editInfo[item._id]?.price && (
                          <div className="tooltip">
                            {editInfo[item._id].price}
                          </div>
                        )}
                      </td>
                      <td>
                        <input
                          type="text"
                          value={item.colorClass}
                          onChange={(e) => handleInputChange(e, item._id, "colorClass")}
                          onFocus={() => setActiveInput(item._id)}
                        />
                        {activeInput === item._id && editInfo[item._id]?.colorClass && (
                          <div className="tooltip">
                            {editInfo[item._id].colorClass}
                          </div>
                        )}
                      </td>
                      <td>
                        <input
                          type="checkbox"
                          checked={item.isTrue}
                          onChange={(e) => handleCheckboxChange(e, item._id)}
                        />
                        <button onClick={() => handleDeleteWithConfirmation(item._id)}>Delete</button>
                      </td>
                    </tr>
                  ))}
                </React.Fragment>
              ))}
            </React.Fragment>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Home;
