import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";
import Login from "./components/Login";
import Register from "./components/Register";
import Home from "./home";

function App() {
  const [auth, setAuth] = useState({
    isAuthenticated: false,
    user: null,
  });

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      // Здесь можно сделать запрос для проверки валидности токена и получения данных пользователя
      setAuth({
        isAuthenticated: true,
        user: { username: "User" }, // Замените это на данные пользователя из вашего API
      });
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    setAuth({ isAuthenticated: false, user: null });
  };

  return (
    <Router>
      <div>
        {auth.isAuthenticated && (
          <nav>
            <span>Welcome, {auth.user.username}</span>
            <button onClick={handleLogout}>Logout</button>
          </nav>
        )}
        <Routes>
          <Route
            path="/login"
            element={
              auth.isAuthenticated ? (
                <Navigate to="/" />
              ) : (
                <Login setAuth={setAuth} />
              )
            }
          />
          <Route
            path="/register"
            element={auth.isAuthenticated ? <Navigate to="/" /> : <Register />}
          />
          <Route
            path="/"
            element={auth.isAuthenticated ? <Home /> : <Navigate to="/login" />}
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
