import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Captcha from "react-simple-captcha"; // Импортируем простую капчу

const Login = ({ setAuth }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [captchaValue, setCaptchaValue] = useState("");
  const [captchaError, setCaptchaError] = useState(false);
  const navigate = useNavigate();
  const apiUrl = "https://bam-app-489c6c1370a9.herokuapp.com";

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (captchaValue !== Captcha.getValue()) {
      setCaptchaError(true);
      return;
    }

    try {
      const response = await axios.post(`${apiUrl}/api/login`, {
        username,
        password,
      });
      document.cookie = `token=${response.data.token}; Secure; HttpOnly; SameSite=Strict`;
      setAuth({
        isAuthenticated: true,
        user: { username },
      });
      setErrorMessage("");
      navigate("/");
    } catch (error) {
      setErrorMessage("Invalid username or password. Please try again.");
    }
  };

  return (
    <div className="login">
      <h1>APCR</h1>
      <h2>Войдите в систему</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Username"
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Password"
        />
        <Captcha onChange={setCaptchaValue} />
        {captchaError && <p style={{ color: "red" }}>Invalid captcha.</p>}
        <button type="submit">Войти</button>
      </form>
      {errorMessage && <p style={{ color: "red" }}>{errorMessage}</p>}
    </div>
  );
};

export default Login;
