import React, { useState, useEffect } from "react";
import axios from "axios";

const Home = () => {
  const [data, setData] = useState([]);
  const [form, setForm] = useState({
    date: "",
    brand: "",
    driver: "",
    customer: "",
    routeNumber: "",
    hours: "",
    isTrue: false,
    licensePlate: "",
    price: "",
  });
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/data");
      setData(response.data);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prevForm) => ({
      ...prevForm,
      [name]: value,
    }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const newData = {
      date: form.date,
      brand: form.brand,
      driver: form.driver,
      customer: form.customer,
      routeNumber: form.routeNumber,
      hours: form.hours,
      isTrue: form.isTrue === "true", // Convert to boolean
      licensePlate: form.licensePlate,
      price: form.price,
    };

    try {
      if (editingId) {
        // Update existing data
        await axios.put(`http://localhost:5000/api/data/${editingId}`, newData);
        setEditingId(null);
      } else {
        // Add new data
        await axios.post("http://localhost:5000/api/data", newData);
      }
      setForm({
        date: "",
        brand: "",
        driver: "",
        customer: "",
        routeNumber: "",
        hours: "",
        isTrue: false,
        licensePlate: "",
        price: "",
      });
      fetchData();
    } catch (error) {
      console.error("Error saving data:", error);
    }
  };

  const handleEdit = (item) => {
    setForm({
      date: item.date,
      brand: item.brand,
      driver: item.driver,
      customer: item.customer,
      routeNumber: item.routeNumber,
      hours: item.hours,
      isTrue: item.isTrue,
      licensePlate: item.licensePlate,
      price: item.price,
    });
    setEditingId(item._id);
  };

  return (
    <div>
      <h1>Data Management</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="date"
          name="date"
          value={form.date}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="brand"
          value={form.brand}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="driver"
          value={form.driver}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="customer"
          value={form.customer}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="routeNumber"
          value={form.routeNumber}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="hours"
          value={form.hours}
          onChange={handleChange}
          required
        />
        <select name="isTrue" value={form.isTrue} onChange={handleChange}>
          <option value={true}>True</option>
          <option value={false}>False</option>
        </select>
        <input
          type="text"
          name="licensePlate"
          value={form.licensePlate}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="price"
          value={form.price}
          onChange={handleChange}
          required
        />
        <button type="submit">{editingId ? "Update Data" : "Add Data"}</button>
      </form>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Brand</th>
            <th>Driver</th>
            <th>Customer</th>
            <th>Route Number</th>
            <th>Hours</th>
            <th>True/False</th>
            <th>License Plate</th>
            <th>Price</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item._id}>
              <td>{new Date(item.date).toLocaleDateString()}</td>
              <td>{item.brand}</td>
              <td>{item.driver}</td>
              <td>{item.customer}</td>
              <td>{item.routeNumber}</td>
              <td>{item.hours}</td>
              <td>{item.isTrue ? "True" : "False"}</td>
              <td>{item.licensePlate}</td>
              <td>{item.price}</td>
              <td>
                <button onClick={() => handleEdit(item)}>Edit</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Home;
