import React, { useEffect, useState } from "react";
import axios from "axios";

const Profile = () => {
  const [data, setData] = useState([]);
  const [uniqueDrivers, setUniqueDrivers] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("/api/your-endpoint"); // Замените на ваш API
        setData(response.data);
      } catch (error) {
        console.error("Ошибка при получении данных:", error);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    const drivers = data.map((item) => item.driver).filter((driver) => driver);
    const unique = [...new Set(drivers)];
    setUniqueDrivers(unique);
  }, [data]);

  return (
    <div>
      <h1>Профиль</h1>
      <h2>Уникальные водители:</h2>
      <ul>
        {uniqueDrivers.length > 0 ? (
          uniqueDrivers.map((driver, index) => <li key={index}>{driver}</li>)
        ) : (
          <li>Нет уникальных водителей</li>
        )}
      </ul>
    </div>
  );
};

export default Profile;
