import React, { useState, useEffect } from "react";
import axios from "axios";
import dayjs from "dayjs"; // Убедитесь, что dayjs установлен и импортирован

const hardcodedData = Array.from({ length: 37 }, (_, index) => ({
  date: dayjs().startOf("day").add(index, "day").format("YYYY-MM-DD"),
  brand: `Brand ${index + 1}`,
  driver: `Driver ${index + 1}`,
  customer: `Customer ${index + 1}`,
  routeNumber: `Route ${index + 1}`,
  hours: `${index + 1}`,
  isTrue: index % 2 === 0,
  vehicleNumber: `Vehicle ${index + 1}`,
  price: `${(index + 1) * 10}`,
}));

const Home = () => {
  const [data, setData] = useState([]);
  const [drivers, setDrivers] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    const fetchDrivers = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/drivers");
        setDrivers(response.data);
      } catch (error) {
        console.error("Error fetching drivers:", error);
      }
    };

    fetchData();
    fetchDrivers();
  }, []);

  const addNewItems = async () => {
    try {
      const promises = hardcodedData.map(async (item) => {
        return await axios.post("http://localhost:5000/api/data", item);
      });
      const responses = await Promise.all(promises);
      setData(responses.map((response) => response.data));
    } catch (error) {
      console.error("Error adding data:", error);
    }
  };

  return (
    <div>
      <h1>Data Table</h1>
      <button onClick={addNewItems}>Add Hardcoded Data</button>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Brand</th>
            <th>Driver</th>
            <th>Customer</th>
            <th>Route Number</th>
            <th>Hours</th>
            <th>True/False</th>
            <th>Vehicle Number</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item._id}>
              <td>{dayjs(item.date).format("YYYY-MM-DD")}</td>
              <td>{item.brand}</td>
              <td>{item.driver}</td>
              <td>{item.customer}</td>
              <td>{item.routeNumber}</td>
              <td>{item.hours}</td>
              <td>{item.isTrue ? "True" : "False"}</td>
              <td>{item.vehicleNumber}</td>
              <td>{item.price}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Home;
